import { useState } from "react";
import { Task, Track } from "@shared/schema";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { ChevronLeft, ChevronRight, Calendar as CalendarIcon } from "lucide-react";
import {
  getDaysInMonth,
  getFirstDayOfMonth,
  getMonthName,
  getTodayString,
  isToday as checkIsToday,
} from "@/lib/utils-date";
import { TASK_TYPES } from "@/lib/constants";
import { cn } from "@/lib/utils";
import { TimeBlockCalendar } from "@/components/time-block-calendar";
import { DragDropTaskList } from "@/components/drag-drop-task-list";

interface CalendarViewProps {
  tasks: Task[];
  tracks: Track[];
  onDateClick: (date: string) => void;
  onTaskClick: (task: Task) => void;
  onTaskUpdate: (taskId: string, updates: Partial<Task>) => void;
}

export function CalendarView({ tasks, tracks, onDateClick, onTaskClick, onTaskUpdate }: CalendarViewProps) {
  const today = new Date();
  const [currentMonth, setCurrentMonth] = useState(today.getMonth());
  const [currentYear, setCurrentYear] = useState(today.getFullYear());
  const [selectedDateStr, setSelectedDateStr] = useState<string | null>(null);

  const daysInMonth = getDaysInMonth(currentYear, currentMonth);
  const firstDay = getFirstDayOfMonth(currentYear, currentMonth);

  const previousMonth = () => {
    if (currentMonth === 0) {
      setCurrentMonth(11);
      setCurrentYear(currentYear - 1);
    } else {
      setCurrentMonth(currentMonth - 1);
    }
  };

  const nextMonth = () => {
    if (currentMonth === 11) {
      setCurrentMonth(0);
      setCurrentYear(currentYear + 1);
    } else {
      setCurrentMonth(currentMonth + 1);
    }
  };

  const goToToday = () => {
    setCurrentMonth(today.getMonth());
    setCurrentYear(today.getFullYear());
  };

  const getTasksForDate = (day: number): Task[] => {
    const dateStr = `${currentYear}-${String(currentMonth + 1).padStart(2, "0")}-${String(day).padStart(2, "0")}`;
    return tasks.filter((task) => {
      const taskDate = task.date;
      const taskEndDate = task.endDate || task.date;
      return dateStr >= taskDate && dateStr <= taskEndDate;
    });
  };

  const getTrackForTask = (task: Task): Track | undefined => {
    return tracks.find((t) => t.id === task.trackId);
  };

  const renderCalendarDays = () => {
    const days = [];

    // Empty cells before first day
    for (let i = 0; i < firstDay; i++) {
      days.push(
        <div key={`empty-${i}`} className="min-h-[120px] bg-muted/30" />
      );
    }

    // Days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      const dateStr = `${currentYear}-${String(currentMonth + 1).padStart(2, "0")}-${String(day).padStart(2, "0")}`;
      const isToday = checkIsToday(dateStr);
      const dayTasks = getTasksForDate(day);

      days.push(
        <div
          key={day}
          className={cn(
            "min-h-[120px] border border-border p-2 cursor-pointer transition-colors hover-elevate",
            "bg-card",
            isToday && "border-primary border-2"
          )}
          onClick={() => handleDateClick(dateStr)}
          data-testid={`calendar-day-${day}`}
        >
          <div className={cn(
            "text-sm font-medium mb-2",
            isToday && "text-primary"
          )}>
            {day}
          </div>
          <div className="space-y-1">
            {dayTasks.slice(0, 3).map((task) => {
              const track = getTrackForTask(task);
              const taskType = TASK_TYPES[task.type];

              return (
                <div
                  key={task.id}
                  className={cn(
                    "text-xs p-1 rounded truncate",
                    "hover-elevate cursor-pointer",
                    task.completed && "opacity-50 line-through"
                  )}
                  style={track ? { backgroundColor: `${track.color}20`, color: track.color } : { backgroundColor: "hsl(var(--secondary))" }}
                  onClick={(e) => {
                    e.stopPropagation();
                    onTaskClick(task);
                  }}
                  data-testid={`calendar-task-${task.id}`}
                >
                  <span className="mr-1">{taskType.icon}</span>
                  {task.title}
                </div>
              );
            })}
            {dayTasks.length > 3 && (
              <div className="text-xs text-muted-foreground">
                +{dayTasks.length - 3} more
              </div>
            )}
          </div>
        </div>
      );
    }

    return days;
  };

  const handleDateClick = (dateStr: string) => {
    setSelectedDateStr(dateStr);
    onDateClick(dateStr);
  };

  const getTasksForSelectedDate = () => {
    if (!selectedDateStr) return [];
    return tasks.filter(task => task.date === selectedDateStr);
  };

  return (
    <div className="space-y-6">
      <Card className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-semibold">
            {getMonthName(currentMonth)} {currentYear}
          </h2>
          <div className="flex items-center gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={goToToday}
              data-testid="button-today"
            >
              <CalendarIcon className="h-4 w-4 mr-2" />
              Today
            </Button>
            <Button
              variant="outline"
              size="icon"
              onClick={previousMonth}
              data-testid="button-previous-month"
            >
              <ChevronLeft className="h-4 w-4" />
            </Button>
            <Button
              variant="outline"
              size="icon"
              onClick={nextMonth}
              data-testid="button-next-month"
            >
              <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        </div>

        <div className="grid grid-cols-7 gap-2">
          {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((day) => (
            <div key={day} className="text-center font-semibold text-sm text-muted-foreground py-2">
              {day}
            </div>
          ))}
          {renderCalendarDays()}
        </div>
      </Card>

      {selectedDateStr && (
        <>
          <Card className="p-6">
            <TimeBlockCalendar
              date={new Date(selectedDateStr)}
              tasks={tasks}
              onTaskClick={onTaskClick}
            />
          </Card>

          <Card className="p-6">
            <DragDropTaskList
              tasks={getTasksForSelectedDate()}
              tracks={tracks}
              onTaskUpdate={onTaskUpdate}
              onTaskClick={onTaskClick}
            />
          </Card>
        </>
      )}
    </div>
  );
}

client/src/components/pomodoro-timer.tsx.content:
import { useState, useEffect, useRef } from "react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Play, Pause, RotateCcw, X } from "lucide-react";
import { POMODORO_PRESETS } from "@/lib/constants";
import { cn } from "@/lib/utils";

interface PomodoroTimerProps {
  onClose: () => void;
  onComplete: (duration: number) => void;
}

type TimerMode = "pomodoro" | "shortBreak" | "longBreak";

export function PomodoroTimer({ onClose, onComplete }: PomodoroTimerProps) {
  const [mode, setMode] = useState<TimerMode>("pomodoro");
  const [timeLeft, setTimeLeft] = useState(POMODORO_PRESETS.pomodoro * 60);
  const [isRunning, setIsRunning] = useState(false);
  const [sessionCount, setSessionCount] = useState(0);
  const intervalRef = useRef<NodeJS.Timeout>();

  const totalTime = POMODORO_PRESETS[mode] * 60;
  const progress = ((totalTime - timeLeft) / totalTime) * 100;

  useEffect(() => {
    if (isRunning && timeLeft > 0) {
      intervalRef.current = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev <= 1) {
            handleTimerComplete();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    } else {
      if (intervalRef.current) clearInterval(intervalRef.current);
    }

    return () => {
      if (intervalRef.current) clearInterval(intervalRef.current);
    };
  }, [isRunning, timeLeft]);

  const handleTimerComplete = () => {
    setIsRunning(false);
    if (mode === "pomodoro") {
      setSessionCount((prev) => prev + 1);
      onComplete(POMODORO_PRESETS.pomodoro);
      playNotificationSound();
    }
  };

  const playNotificationSound = () => {
    // Browser notification sound (simple beep)
    const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
    const oscillator = audioContext.createOscillator();
    const gainNode = audioContext.createGain();

    oscillator.connect(gainNode);
    gainNode.connect(audioContext.destination);

    oscillator.frequency.value = 800;
    oscillator.type = "sine";

    gainNode.gain.setValueAtTime(0.3, audioContext.currentTime);
    gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.5);

    oscillator.start(audioContext.currentTime);
    oscillator.stop(audioContext.currentTime + 0.5);
  };

  const handleModeChange = (newMode: TimerMode) => {
    setMode(newMode);
    setTimeLeft(POMODORO_PRESETS[newMode] * 60);
    setIsRunning(false);
  };

  const handleReset = () => {
    setTimeLeft(POMODORO_PRESETS[mode] * 60);
    setIsRunning(false);
  };

  const formatTime = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, "0")}:${secs.toString().padStart(2, "0")}`;
  };

  return (
    <div className="fixed inset-0 bg-background/80 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <Card className="w-full max-w-md p-8 space-y-6 relative">
        <Button
          variant="ghost"
          size="icon"
          className="absolute top-4 right-4"
          onClick={onClose}
          data-testid="button-close-pomodoro"
        >
          <X className="h-5 w-5" />
        </Button>

        <div className="text-center">
          <h2 className="text-2xl font-semibold mb-2">Pomodoro Timer</h2>
          <p className="text-sm text-muted-foreground">
            Sessions completed: {sessionCount}
          </p>
        </div>

        <div className="flex gap-2">
          <Button
            variant={mode === "pomodoro" ? "default" : "outline"}
            onClick={() => handleModeChange("pomodoro")}
            className="flex-1"
            data-testid="button-mode-pomodoro"
          >
            Pomodoro
          </Button>
          <Button
            variant={mode === "shortBreak" ? "default" : "outline"}
            onClick={() => handleModeChange("shortBreak")}
            className="flex-1"
            data-testid="button-mode-short-break"
          >
            Short Break
          </Button>
          <Button
            variant={mode === "longBreak" ? "default" : "outline"}
            onClick={() => handleModeChange("longBreak")}
            className="flex-1"
            data-testid="button-mode-long-break"
          >
            Long Break
          </Button>
        </div>

        <div className="relative">
          <div className="flex items-center justify-center h-48">
            <div
              className={cn(
                "relative w-40 h-40 rounded-full flex items-center justify-center",
                "border-8 border-primary/20",
                isRunning && "animate-pulse"
              )}
            >
              <div className="text-center">
                <div className="text-5xl font-bold" data-testid="text-timer">
                  {formatTime(timeLeft)}
                </div>
                <div className="text-sm text-muted-foreground mt-2">
                  {mode === "pomodoro" ? "Focus Time" : mode === "shortBreak" ? "Short Break" : "Long Break"}
                </div>
              </div>
            </div>
          </div>
          <Progress value={progress} className="mt-4" />
        </div>

        <div className="flex gap-2 justify-center">
          <Button
            size="lg"
            onClick={() => setIsRunning(!isRunning)}
            data-testid="button-toggle-timer"
            className="w-32"
          >
            {isRunning ? (
              <>
                <Pause className="h-5 w-5 mr-2" />
                Pause
              </>
            ) : (
              <>
                <Play className="h-5 w-5 mr-2" />
                Start
              </>
            )}
          </Button>
          <Button
            size="lg"
            variant="outline"
            onClick={handleReset}
            data-testid="button-reset-timer"
          >
            <RotateCcw className="h-5 w-5 mr-2" />
            Reset
          </Button>
        </div>
      </Card>
    </div>
  );
}

client/src/components/track-dialog.tsx.content:
import { useState, useEffect } from "react";
import { Track, InsertTrack } from "@shared/schema";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";

interface TrackDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  track?: Track;
  onSave: (track: InsertTrack | (Track & Partial<InsertTrack>)) => void;
}

const PRESET_COLORS = [
  "#8b5cf6", // Purple
  "#ef4444", // Red
  "#3b82f6", // Blue
  "#f59e0b", // Amber
  "#10b981", // Green
  "#ec4899", // Pink
  "#06b6d4", // Cyan
  "#f97316", // Orange
];

export function TrackDialog({ open, onOpenChange, track, onSave }: TrackDialogProps) {
  const [formData, setFormData] = useState<InsertTrack>({
    name: "",
    color: PRESET_COLORS[0],
  });

  useEffect(() => {
    if (track) {
      setFormData({ name: track.name, color: track.color });
    } else {
      setFormData({ name: "", color: PRESET_COLORS[0] });
    }
  }, [track, open]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (track) {
      onSave({ ...track, ...formData });
    } else {
      onSave(formData);
    }
    onOpenChange(false);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent data-testid="dialog-track">
        <DialogHeader>
          <DialogTitle>{track ? "Edit Track" : "Create New Track"}</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="name">Track Name *</Label>
            <Input
              id="name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              required
              data-testid="input-track-name"
              placeholder="e.g., Computer Science, Mathematics"
            />
          </div>

          <div className="space-y-2">
            <Label>Color</Label>
            <div className="grid grid-cols-8 gap-2">
              {PRESET_COLORS.map((color) => (
                <button
                  key={color}
                  type="button"
                  className="h-10 w-10 rounded-lg border-2 transition-all hover:scale-110"
                  style={{
                    backgroundColor: color,
                    borderColor: formData.color === color ? "hsl(var(--ring))" : "transparent",
                  }}
                  onClick={() => setFormData({ ...formData, color })}
                  data-testid={`color-${color}`}
                />
              ))}
            </div>
            <div className="flex items-center gap-2 mt-2">
              <Label htmlFor="customColor" className="text-sm">Custom:</Label>
              <Input
                id="customColor"
                type="color"
                value={formData.color}
                onChange={(e) => setFormData({ ...formData, color: e.target.value })}
                className="w-20 h-10"
                data-testid="input-custom-color"
              />
            </div>
          </div>

          <DialogFooter>
            <Button type="button" variant="outline" onClick={() => onOpenChange(false)} data-testid="button-cancel">
              Cancel
            </Button>
            <Button type="submit" data-testid="button-save-track">
              {track ? "Update Track" : "Create Track"}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

