import { useMemo, useRef } from "react";
import { format, parseISO } from "date-fns";
import type { Task } from "@shared/schema";
import { TASK_TYPES } from "@/lib/constants";
import { Button } from "@/components/ui/button";
import { Download } from "lucide-react";
import html2canvas from "html2canvas";

interface TimeBlockCalendarProps {
  date: Date;
  tasks: Task[];
  onTaskClick?: (task: Task) => void;
}

export function TimeBlockCalendar({ date, tasks, onTaskClick }: TimeBlockCalendarProps) {
  const calendarRef = useRef<HTMLDivElement>(null);

  const hours = Array.from({ length: 24 }, (_, i) => i);

  const tasksForDate = useMemo(() => {
    const dateStr = format(date, "yyyy-MM-dd");
    return tasks.filter(task => {
      if (task.date === dateStr && task.startTime && task.endTime) {
        return true;
      }
      return false;
    });
  }, [date, tasks]);

  const getTaskPosition = (task: Task) => {
    if (!task.startTime || !task.endTime) return null;

    const [startHour, startMin] = task.startTime.split(":").map(Number);
    const [endHour, endMin] = task.endTime.split(":").map(Number);

    const top = (startHour + startMin / 60) * 60;
    const duration = (endHour + endMin / 60) - (startHour + startMin / 60);
    const height = duration * 60;

    return { top, height };
  };

  const exportAsPNG = async () => {
    if (!calendarRef.current) return;

    try {
      const canvas = await html2canvas(calendarRef.current, {
        backgroundColor: "#ffffff",
        scale: 2,
      });

      const link = document.createElement("a");
      link.download = `schedule-${format(date, "yyyy-MM-dd")}.png`;
      link.href = canvas.toDataURL();
      link.click();
    } catch (error) {
      console.error("Failed to export calendar:", error);
    }
  };

  return (
    <div className="space-y-4">
      <div className="flex justify-between items-center">
        <h3 className="text-lg font-semibold">Time Blocks - {format(date, "MMMM d, yyyy")}</h3>
        <Button onClick={exportAsPNG} variant="outline" size="sm">
          <Download className="w-4 h-4 mr-2" />
          Export as PNG
        </Button>
      </div>

      <div 
        ref={calendarRef}
        className="bg-white rounded-lg border border-border p-4 relative"
        style={{ minHeight: "1440px" }}
      >
        {hours.map((hour) => (
          <div 
            key={hour}
            className="relative h-[60px] border-b border-gray-200"
            style={{ top: `${hour * 60}px` }}
          >
            <div className="absolute left-0 top-0 w-16 text-sm text-muted-foreground font-medium">
              {format(new Date().setHours(hour, 0, 0, 0), "h:mm a")}
            </div>
          </div>
        ))}

        <div className="absolute top-0 left-20 right-0" style={{ height: "1440px" }}>
          {tasksForDate.map((task) => {
            const position = getTaskPosition(task);
            if (!position) return null;

            const taskType = TASK_TYPES[task.type as keyof typeof TASK_TYPES];

            return (
              <div
                key={task.id}
                className="absolute left-0 right-0 mx-2 rounded-lg p-2 cursor-pointer hover:opacity-90 transition-opacity"
                style={{
                  top: `${position.top}px`,
                  height: `${position.height}px`,
                  backgroundColor: taskType?.color || "#8b5cf6",
                  color: "white",
                  zIndex: 10,
                }}
                onClick={() => onTaskClick?.(task)}
              >
                <div className="text-sm font-semibold truncate">
                  {taskType?.icon} {task.title}
                </div>
                {position.height > 40 && (
                  <div className="text-xs opacity-90 mt-1 truncate">
                    {task.startTime} - {task.endTime}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

client/src/components/drag-drop-task-lis.content:
import { useState, useEffect } from "react";
import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
  DragEndEvent,
} from "@dnd-kit/core";
import {
  arrayMove,
  SortableContext,
  sortableKeyboardCoordinates,
  useSortable,
  verticalListSortingStrategy,
} from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import type { Task, Track } from "@shared/schema";
import { TASK_TYPES } from "@/lib/constants";
import { GripVertical, Clock, CheckCircle2, Circle } from "lucide-react";
import { Checkbox } from "@/components/ui/checkbox";

interface DragDropTaskListProps {
  tasks: Task[];
  tracks: Track[];
  onTaskUpdate: (taskId: string, updates: Partial<Task>) => void;
  onTaskClick?: (task: Task) => void;
}

function SortableTaskItem({ 
  task, 
  track,
  onToggle,
  onClick 
}: { 
  task: Task;
  track?: Track;
  onToggle: () => void;
  onClick: () => void;
}) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id: task.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  const taskType = TASK_TYPES[task.type as keyof typeof TASK_TYPES];

  return (
    <div
      ref={setNodeRef}
      style={style}
      className="flex items-center gap-3 p-3 bg-card rounded-lg border border-border hover:border-primary/50 transition-colors"
    >
      <div {...attributes} {...listeners} className="cursor-grab active:cursor-grabbing">
        <GripVertical className="w-4 h-4 text-muted-foreground" />
      </div>

      <Checkbox
        checked={task.completed}
        onCheckedChange={onToggle}
      />

      <div className="flex-1 min-w-0 cursor-pointer" onClick={onClick}>
        <div className="flex items-center gap-2">
          <span className="text-lg">{taskType?.icon}</span>
          <span className={`font-medium ${task.completed ? "line-through text-muted-foreground" : ""}`}>
            {task.title}
          </span>
          {track && (
            <span 
              className="text-xs px-2 py-0.5 rounded-full text-white"
              style={{ backgroundColor: track.color }}
            >
              {track.name}
            </span>
          )}
        </div>
        {task.startTime && task.endTime && (
          <div className="flex items-center gap-1 text-sm text-muted-foreground mt-1">
            <Clock className="w-3 h-3" />
            {task.startTime} - {task.endTime}
          </div>
        )}
      </div>

      {task.priority === "high" && (
        <div className="text-xs font-semibold text-destructive">HIGH</div>
      )}
    </div>
  );
}

export function DragDropTaskList({ tasks, tracks, onTaskUpdate, onTaskClick }: DragDropTaskListProps) {
  const [sortedTasks, setSortedTasks] = useState(tasks);

  useEffect(() => {
    setSortedTasks(tasks);
  }, [tasks]);

  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  const handleDragEnd = (event: DragEndEvent) => {
    const { active, over } = event;

    if (over && active.id !== over.id) {
      setSortedTasks((items) => {
        const oldIndex = items.findIndex((item) => item.id === active.id);
        const newIndex = items.findIndex((item) => item.id === over.id);
        const newOrder = arrayMove(items, oldIndex, newIndex);

        // Update order in backend for all affected tasks
        newOrder.forEach((task, index) => {
          if ((task.order || 0) !== index) {
            onTaskUpdate(task.id, { order: index });
          }
        });

        return newOrder;
      });
    }
  };

  const handleToggle = (task: Task) => {
    onTaskUpdate(task.id, { completed: !task.completed });
  };

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold">Rearrange Tasks</h3>
      <DndContext
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragEnd={handleDragEnd}
      >
        <SortableContext
          items={sortedTasks.map(t => t.id)}
          strategy={verticalListSortingStrategy}
        >
          <div className="space-y-2">
            {sortedTasks.length === 0 ? (
              <div className="text-center text-muted-foreground py-8">
                No tasks for this day
              </div>
            ) : (
              sortedTasks.map((task) => {
                const track = tracks.find(t => t.id === task.trackId);
                return (
                  <SortableTaskItem
                    key={task.id}
                    task={task}
                    track={track}
                    onToggle={() => handleToggle(task)}
                    onClick={() => onTaskClick?.(task)}
                  />
                );
              })
            )}
          </div>
        </SortableContext>
      </DndContext>
    </div>
  );
}

client/src/components/horizontal-timelin.content:
import { useMemo, useState } from "react";
import { format, addDays, startOfDay, isSameDay, parseISO } from "date-fns";
import type { Task, Track } from "@shared/schema";
import { TASK_TYPES } from "@/lib/constants";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { Button } from "@/components/ui/button";

interface HorizontalTimelineProps {
  tasks: Task[];
  tracks: Track[];
  onTaskClick?: (task: Task) => void;
}

export function HorizontalTimeline({ tasks, tracks, onTaskClick }: HorizontalTimelineProps) {
  const [selectedTrackId, setSelectedTrackId] = useState<string>("all");
  const [startDate, setStartDate] = useState(new Date());

  const days = useMemo(() => {
    return Array.from({ length: 14 }, (_, i) => addDays(startDate, i));
  }, [startDate]);

  const filteredTasks = useMemo(() => {
    let filtered = tasks;

    if (selectedTrackId !== "all") {
      filtered = filtered.filter(task => task.trackId === selectedTrackId);
    }

    const endDate = addDays(startDate, 14);
    filtered = filtered.filter(task => {
      const taskDate = parseISO(task.date);
      return taskDate >= startOfDay(startDate) && taskDate < endDate;
    });

    return filtered.sort((a, b) => a.date.localeCompare(b.date));
  }, [tasks, selectedTrackId, startDate]);

  const getTasksForDay = (day: Date) => {
    return filteredTasks.filter(task => isSameDay(parseISO(task.date), day));
  };

  const goToPreviousWeek = () => {
    setStartDate(prev => addDays(prev, -7));
  };

  const goToNextWeek = () => {
    setStartDate(prev => addDays(prev, 7));
  };

  const goToToday = () => {
    setStartDate(new Date());
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between gap-4">
        <h3 className="text-lg font-semibold">Upcoming Tasks Timeline (2 Weeks)</h3>
        <div className="flex items-center gap-2">
          <Select value={selectedTrackId} onValueChange={setSelectedTrackId}>
            <SelectTrigger className="w-48">
              <SelectValue placeholder="Filter by track" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Tracks</SelectItem>
              {tracks.map((track) => (
                <SelectItem key={track.id} value={track.id}>
                  <div className="flex items-center gap-2">
                    <div 
                      className="w-3 h-3 rounded-full" 
                      style={{ backgroundColor: track.color }}
                    />
                    {track.name}
                  </div>
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
          <Button variant="outline" size="sm" onClick={goToPreviousWeek}>
            <ChevronLeft className="w-4 h-4" />
          </Button>
          <Button variant="outline" size="sm" onClick={goToToday}>
            Today
          </Button>
          <Button variant="outline" size="sm" onClick={goToNextWeek}>
            <ChevronRight className="w-4 h-4" />
          </Button>
        </div>
      </div>

      <div className="overflow-x-auto">
        <div className="flex gap-2 min-w-max pb-4">
          {days.map((day) => {
            const dayTasks = getTasksForDay(day);
            const isToday = isSameDay(day, new Date());

            return (
              <div 
                key={day.toISOString()}
                className={`flex-shrink-0 w-40 rounded-lg border p-3 ${
                  isToday ? "border-primary bg-primary/5" : "border-border"
                }`}
              >
                <div className="text-center mb-3">
                  <div className="text-sm font-semibold">
                    {format(day, "EEE")}
                  </div>
                  <div className={`text-2xl font-bold ${isToday ? "text-primary" : ""}`}>
                    {format(day, "d")}
                  </div>
                  <div className="text-xs text-muted-foreground">
                    {format(day, "MMM yyyy")}
                  </div>
                </div>

                <div className="space-y-2">
                  {dayTasks.length === 0 ? (
                    <div className="text-xs text-muted-foreground text-center py-2">
                      No tasks
                    </div>
                  ) : (
                    dayTasks.map((task) => {
                      const taskType = TASK_TYPES[task.type as keyof typeof TASK_TYPES];
                      const track = tracks.find(t => t.id === task.trackId);

                      return (
                        <div
                          key={task.id}
                          className="rounded p-2 cursor-pointer hover:opacity-80 transition-opacity text-white text-xs"
                          style={{ backgroundColor: taskType?.color || "#8b5cf6" }}
                          onClick={() => onTaskClick?.(task)}
                        >
                          <div className="font-semibold truncate">
                            {taskType?.icon} {task.title}
                          </div>
                          {task.startTime && (
                            <div className="text-xs opacity-90 mt-1">
                              {task.startTime}
                            </div>
                          )}
                          {track && (
                            <Badge 
                              className="mt-1 text-[10px] px-1 py-0"
                              style={{ 
                                backgroundColor: track.color,
                                borderColor: track.color 
                              }}
                            >
                              {track.name}
                            </Badge>
                          )}
                        </div>
                      );
                    })
                  )}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
